                                       {% set c=c+1 %}
                                   {% endif %}
                                   {% endfor %}
</th>
                              <th>{{bien.description}}</th>
                                <th>{{\"action\"}}</th>
                 
     
                         </tr>
                    {% endfor %}
                    
                                     
                                    </tbody>
                                </table>

</form>
